package net.sourceforge.simcpux.customviewsawtoothstyle;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.widget.Toast;public class DemoActivity5 extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_topbar);        Topbar topbar= (Topbar) findViewById(R.id.topbar);        topbar.setOnTopbarClickListener(new Topbar.topbarClickListenter() {            @Override            public void leftClick() {                Toast.makeText(DemoActivity5.this,"left",Toast.LENGTH_SHORT).show();            }            @Override            public void rightClick() {                Toast.makeText(DemoActivity5.this,"right",Toast.LENGTH_SHORT).show();            }        });        topbar.setLeftButtonIsVisable(false);    }}